    68→  }
    69→  static async updateAuftragsbestaetigungEntry(id: string, fields: Partial<Auftragsbestaetigung['fields']>) {
    70→    return callApi('PATCH', `/apps/${APP_IDS.AUFTRAGSBESTAETIGUNG}/records/${id}`, { fields });
    71→  }
    72→  static async deleteAuftragsbestaetigungEntry(id: string) {
