              {/* Comparisons table */}
              <Card className="shadow-sm">
                <CardHeader>
                  <CardTitle className="text-lg">Letzte Abgleiche</CardTitle>
                </CardHeader>
                <CardContent>
                  {enrichedAbgleiche.length === 0 ? (
                    <EmptyState onAction={() => setDialogOpen(true)} />
