    45→
    46→// Semi-circular gauge component for the hero KPI
    47→function MatchRateGauge({
    48→  percentage,
    49→  total,
    50→  size = 280,
    51→  animate = true
    52→}: {
    53→  percentage: number;
    54→  total: number;
    55→  size?: number;
    56→  animate?: boolean;
    57→}) {
    58→  const [displayPercentage, setDisplayPercentage] = useState(0);
