   241→// New comparison dialog
   242→function NewComparisonDialog({
   243→  bestellungen,
   244→  auftragsbestaetigungen,
   245→  onSubmit,
   246→  open,
   247→  onOpenChange
   248→}: {
   249→  bestellungen: Bestellung[];
   250→  auftragsbestaetigungen: Auftragsbestaetigung[];
   251→  onSubmit: (bestellungId: string, abId: string) => Promise<void>;
   252→  open: boolean;
   253→  onOpenChange: (open: boolean) => void;
   254→}) {
   255→  const [selectedBestellung, setSelectedBestellung] = useState<string>('');
   256→  const [selectedAB, setSelectedAB] = useState<string>('');
   257→  const [submitting, setSubmitting] = useState(false);
   258→
   259→  const handleSubmit = async () => {
   260→    if (!selectedBestellung || !selectedAB) return;
   261→    setSubmitting(true);
   262→    try {
   263→      await onSubmit(selectedBestellung, selectedAB);
   264→      setSelectedBestellung('');
   265→      setSelectedAB('');
   266→      onOpenChange(false);
   267→    } catch (err) {
   268→      console.error('Failed to create comparison:', err);
   269→    } finally {
   270→      setSubmitting(false);
   271→    }
   272→  };
   273→
   274→  return (
   275→    <Dialog open={open} onOpenChange={onOpenChange}>
   276→      <DialogContent className="sm:max-w-md">
   277→        <DialogHeader>
   278→          <DialogTitle>Neuen Abgleich starten</DialogTitle>
   279→        </DialogHeader>
   280→        <div className="space-y-4 pt-4">
   281→          <div className="space-y-2">
   282→            <Label htmlFor="bestellung">Bestellung auswählen</Label>
   283→            <Select value={selectedBestellung} onValueChange={setSelectedBestellung}>
   284→              <SelectTrigger id="bestellung">
   285→                <SelectValue placeholder="Bestellung wählen..." />
   286→              </SelectTrigger>
   287→              <SelectContent>
   288→                {bestellungen.map((b) => (
   289→                  <SelectItem key={b.record_id} value={b.record_id}>
   290→                    {b.fields.bestellung_nummer || 'Ohne Nummer'} - {b.fields.bestellung_lieferant || 'Unbekannter Lieferant'}
   291→                  </SelectItem>
   292→                ))}
   293→              </SelectContent>
   294→            </Select>
   295→          </div>
   296→
   297→          <div className="space-y-2">
   298→            <Label htmlFor="ab">Auftragsbestätigung auswählen</Label>
   299→            <Select value={selectedAB} onValueChange={setSelectedAB}>
   300→              <SelectTrigger id="ab">
   301→                <SelectValue placeholder="Auftragsbestätigung wählen..." />
   302→              </SelectTrigger>
   303→              <SelectContent>
   304→                {auftragsbestaetigungen.map((ab) => (
   305→                  <SelectItem key={ab.record_id} value={ab.record_id}>
   306→                    {ab.fields.ab_nummer || 'Ohne Nummer'} - {ab.fields.ab_lieferant || 'Unbekannter Lieferant'}
   307→                  </SelectItem>
   308→                ))}
   309→              </SelectContent>
   310→            </Select>
   311→          </div>
   312→
   313→          <Button
   314→            onClick={handleSubmit}
   315→            disabled={!selectedBestellung || !selectedAB || submitting}
   316→            className="w-full"
   317→          >
   318→            {submitting ? (
   319→              <>
   320→                <RefreshCw className="w-4 h-4 mr-2 animate-spin" />
   321→                Wird gestartet...
   322→              </>
   323→            ) : (
   324→              <>
   325→                <Plus className="w-4 h-4 mr-2" />
   326→                Abgleich starten
   327→              </>
   328→            )}
   329→          </Button>
   330→        </div>
   331→      </DialogContent>
   332→    </Dialog>
   333→  );
   334→}
