   412→// Main Dashboard component
   413→export default function Dashboard() {
   414→  const [abgleiche, setAbgleiche] = useState<AutomatischerAbgleich[]>([]);
   415→  const [bestellungen, setBestellungen] = useState<Bestellung[]>([]);
   416→  const [auftragsbestaetigungen, setAuftragsbestaetigungen] = useState<Auftragsbestaetigung[]>([]);
   417→  const [loading, setLoading] = useState(true);
   418→  const [error, setError] = useState<Error | null>(null);
   419→  const [dialogOpen, setDialogOpen] = useState(false);
   420→  const [selectedComparison, setSelectedComparison] = useState<AutomatischerAbgleich | null>(null);
   421→  const [detailDialogOpen, setDetailDialogOpen] = useState(false);
   422→
   423→  const fetchData = async () => {
   424→    try {
   425→      setLoading(true);
   426→      setError(null);
   427→      const [abgleicheData, bestellungenData, abData] = await Promise.all([
   428→        LivingAppsService.getAutomatischerAbgleich(),
   429→        LivingAppsService.getBestellung(),
   430→        LivingAppsService.getAuftragsbestaetigung(),
   431→      ]);
   432→      setAbgleiche(abgleicheData);
   433→      setBestellungen(bestellungenData);
   434→      setAuftragsbestaetigungen(abData);
   435→    } catch (err) {
   436→      setError(err instanceof Error ? err : new Error('Unbekannter Fehler'));
   437→    } finally {
   438→      setLoading(false);
   439→    }
   440→  };
   441→
   442→  useEffect(() => {
   443→    fetchData();
   444→  }, []);
   445→
   446→  // Create lookup maps
   447→  const bestellungMap = useMemo(() => {
   448→    const map = new Map<string, Bestellung>();
   449→    bestellungen.forEach(b => map.set(b.record_id, b));
   450→    return map;
   451→  }, [bestellungen]);
   452→
   453→  const abMap = useMemo(() => {
   454→    const map = new Map<string, Auftragsbestaetigung>();
   455→    auftragsbestaetigungen.forEach(ab => map.set(ab.record_id, ab));
   456→    return map;
   457→  }, [auftragsbestaetigungen]);
   458→
   459→  // Calculate KPIs
   460→  const stats = useMemo(() => {
   461→    const total = abgleiche.length;
   462→    const matching = abgleiche.filter(a => a.fields.abgleich_status === true).length;
   463→    const withDiscrepancies = abgleiche.filter(a => a.fields.abgleich_status === false).length;
   464→    const matchRate = total > 0 ? (matching / total) * 100 : 0;
   465→    return { total, matching, withDiscrepancies, matchRate };
   466→  }, [abgleiche]);
   467→
   468→  // Enrich comparisons with linked data
   469→  const enrichedAbgleiche = useMemo(() => {
   470→    return abgleiche
   471→      .map(abgleich => {
   472→        const bestellungId = extractRecordId(abgleich.fields.abgleich_bestellung);
   473→        const abId = extractRecordId(abgleich.fields.abgleich_ab);
   474→        return {
   475→          ...abgleich,
   476→          bestellung: bestellungId ? bestellungMap.get(bestellungId) : null,
   477→          auftragsbestaetigung: abId ? abMap.get(abId) : null,
   478→        };
   479→      })
   480→      .sort((a, b) => new Date(b.createdat).getTime() - new Date(a.createdat).getTime());
   481→  }, [abgleiche, bestellungMap, abMap]);
   482→
   483→  // Handle new comparison
   484→  const handleNewComparison = async (bestellungId: string, abId: string) => {
   485→    await LivingAppsService.createAbgleichStartenEntry({
   486→      abgleich_bestellung: createRecordUrl(APP_IDS.BESTELLUNG, bestellungId),
   487→      abgleich_ab: createRecordUrl(APP_IDS.AUFTRAGSBESTAETIGUNG, abId),
   488→    });
   489→    // Refresh data after creating
