   504→    : null;
   505→  const selectedAB = selectedComparison
   506→    ? abMap.get(extractRecordId(selectedComparison.fields.abgleich_ab) || '')
   507→    : null;
   508→
   509→  return (
   510→    <div className="min-h-screen bg-background">
