            {enrichedAbgleiche.length === 0 ? (
              <EmptyState onAction={() => setDialogOpen(true)} />
            ) : (
              <div className="space-y-2">
                {enrichedAbgleiche.slice(0, 10).map((abgleich) => (
                  <Card
                    key={abgleich.record_id}
