  TableRow,
} from '@/components/ui/table';
import { Tabs, TabsContent, TabsList, TabsTrigger } from '@/components/ui/tabs';
import { Label } from '@/components/ui/label';
import {
  CheckCircle2,
  AlertCircle,
  Plus,
  ChevronRight,
  FileText,
  ClipboardList,
  RefreshCw,
  BarChart3
} from 'lucide-react';
import { format, parseISO } from 'date-fns';
import { de } from 'date-fns/locale';

// Semi-circular gauge component for the hero KPI
function MatchRateGauge({
  percentage,
  total,
  size = 280,
  animate = true
}: {
  percentage: number;
  total: number;
  size?: number;
  animate?: boolean;
}) {
  const [displayPercentage, setDisplayPercentage] = useState(0);
  const strokeWidth = size > 250 ? 12 : 10;
  const radius = (size - strokeWidth) / 2;
  const circumference = Math.PI * radius; // Half circle

  useEffect(() => {
    if (animate) {
      const timer = setTimeout(() => {
        setDisplayPercentage(percentage);
      }, 100);
      return () => clearTimeout(timer);
    } else {
      setDisplayPercentage(percentage);
    }
  }, [percentage, animate]);

  const strokeDashoffset = circumference - (displayPercentage / 100) * circumference;

  return (
    <div className="flex flex-col items-center">
      <svg
        width={size}
        height={size / 2 + 20}
        viewBox={`0 0 ${size} ${size / 2 + 20}`}
        className="overflow-visible"
      >
        {/* Background arc */}
        <path
          d={`M ${strokeWidth / 2} ${size / 2} A ${radius} ${radius} 0 0 1 ${size - strokeWidth / 2} ${size / 2}`}
          fill="none"
          stroke="hsl(var(--muted))"
          strokeWidth={strokeWidth}
          strokeLinecap="round"
        />
        {/* Progress arc */}
        <path
          d={`M ${strokeWidth / 2} ${size / 2} A ${radius} ${radius} 0 0 1 ${size - strokeWidth / 2} ${size / 2}`}
          fill="none"
          stroke="hsl(var(--primary))"
          strokeWidth={strokeWidth}
          strokeLinecap="round"
          strokeDasharray={circumference}
          strokeDashoffset={strokeDashoffset}
          className="transition-all duration-700 ease-out"
          style={{
            filter: 'drop-shadow(0 0 6px hsl(173 58% 39% / 0.3))'
          }}
        />
        {/* Percentage text */}
        <text
          x={size / 2}
          y={size / 2 - 10}
          textAnchor="middle"
          className="fill-foreground font-bold"
          style={{ fontSize: size > 250 ? '64px' : '56px' }}
        >
          {Math.round(displayPercentage)}%
        </text>
      </svg>
      <p className="text-sm text-muted-foreground -mt-2">
        von {total} Abgleichen
      </p>
    </div>
  );
}

// Status badge component
function StatusBadge({ isMatch }: { isMatch: boolean | null | undefined }) {
  if (isMatch === true) {
    return (
      <Badge
        variant="outline"
        className="bg-[hsl(158_64%_42%/0.1)] text-[hsl(158_64%_32%)] border-[hsl(158_64%_42%/0.3)]"
      >
        <CheckCircle2 className="w-3 h-3 mr-1" />
        Übereinstimmend
      </Badge>
    );
  }
  return (
    <Badge
      variant="outline"
      className="bg-[hsl(38_92%_50%/0.1)] text-[hsl(38_92%_35%)] border-[hsl(38_92%_50%/0.3)]"
    >
      <AlertCircle className="w-3 h-3 mr-1" />
      Abweichungen
    </Badge>
  );
}

// Loading skeleton for the dashboard
function LoadingSkeleton() {
  return (
    <div className="min-h-screen bg-background p-4 md:p-6">
      <div className="max-w-7xl mx-auto space-y-6">
        <Skeleton className="h-8 w-48" />

        <div className="grid grid-cols-1 lg:grid-cols-[1fr_320px] gap-6">
          <div className="space-y-6">
            <Card>
              <CardContent className="pt-6">
                <div className="flex justify-center">
                  <Skeleton className="h-[160px] w-[280px] rounded-full" />
                </div>
              </CardContent>
            </Card>

            <div className="grid grid-cols-3 gap-4">
              {[1, 2, 3].map((i) => (
